$(document).ready(function(){


	

// SlideHeader
(function () {
var header = new Headroom(document.querySelector("header"), {
tolerance: 5,
offset: 205,
classes: {
initial: "animated",
pinned: "slideDown",
unpinned: "slideUp"
}
});
header.init();
}());


// Back To Top
$(".back-to-top").click(function () {
$("html,body").animate({
scrollTop: 0
}, 10);
});

	
// Label For Input
$('.form-outline .form-control').on('keyup', function () {
var self = $(this),
label = self.siblings('label');
if (self.val() != '') {
label.addClass('active');
} else {
label.removeClass('active');
}
});


// Label For Select
$('.form-outline .form-control.selectpicker').on('changed.bs.select', function () {
var labelselect = $('label[for="' + $(this).attr('id') + '"]');
$(labelselect).addClass('active');
});
	

// Label For Datetimepicker
$('.form-outline .form-control.datetimepicker').on('changed.datetimepicker', function(){
var labelselect = $('label[for="' + $(this).attr('id') + '"]');
$(labelselect).addClass('active');
});



// Hide Show Password
$(".toggle-password").click(function () {
$(this).toggleClass("eye eye-slash");
var input = $($(this).attr("toggle"));
if (input.attr("type") == "password") {
input.attr("type", "text");
} else {
input.attr("type", "password");
}
});


});


